div.script:
//
document.addEventListener('DOMContentLoaded', function() {
    const form = document.getElementById('ifn-signup-form');
    const steps = document.querySelectorAll('.ifn-form-step');
    const stepDots = document.querySelectorAll('.step-dot');
    const prevBtn = document.getElementById('prevStep');
    const nextBtn = document.getElementById('nextStep');
    const submitBtn = document.getElementById('submitBtn');
    const currentStepInput = document.getElementById('currentStep');

    let currentStep = 1;
    const totalSteps = 4;

    // Navigation entre etapes
    function showStep(step) {
        steps.forEach(function(s, index) {
            s.classList.toggle('active', index + 1 === step);
        });

        stepDots.forEach(function(dot, index) {
            dot.classList.toggle('active', index + 1 === step);
            dot.classList.toggle('completed', index + 1 &lt; step);
        });

        // Mettre a jour les boutons
        prevBtn.disabled = step === 1;
        nextBtn.style.display = step === totalSteps ? 'none' : 'inline-flex';
        submitBtn.style.display = step === totalSteps ? 'inline-flex' : 'none';

        currentStepInput.value = step;
        currentStep = step;
    }

    // Validation d'√©tape
    function validateStep(step) {
        const currentStepElement = document.querySelector(`.ifn-form-step[data-step="${step}"]`);
        const requiredFields = currentStepElement.querySelectorAll('[required]');

        for (let field of requiredFields) {
            if (!field.value.trim()) {
                field.focus();
                field.classList.add('is-invalid');
                return false;
            }
            field.classList.remove('is-invalid');
        }

        // Validation sp√©ciale pour le mot de passe
        if (step === 4) {
            const password = document.getElementById('password').value;
            const confirmPassword = document.getElementById('confirm_password').value;

            if (password !== confirmPassword) {
                document.getElementById('confirm_password').focus();
                document.getElementById('confirm_password').classList.add('is-invalid');
                return false;
            }
        }

        return true;
    }

    // √âv√©nements des boutons
    nextBtn.addEventListener('click', function() {
        if (validateStep(currentStep)) {
            showStep(currentStep + 1);
        }
    });

    prevBtn.addEventListener('click', function() {
        showStep(currentStep - 1);
    });

    // S√©lection du type de membre
    document.querySelectorAll('.member-type-input').forEach(function(input) {
        input.addEventListener('change', function() {
            document.querySelectorAll('.member-type-card').forEach(function(card) {
                card.classList.remove('selected');
            });
            if (this.checked) {
                this.closest('.member-type-card').classList.add('selected');
            }
        });
    });

    // Force du mot de passe
    const passwordInput = document.getElementById('password');
    const strengthFill = document.querySelector('.strength-fill');
    const strengthText = document.querySelector('.strength-text');

    passwordInput.addEventListener('input', function() {
        const password = this.value;
        let strength = 0;

        if (password.length >= 8) strength++;
        if (password.match(/[a-z]/) && password.match(/[A-Z]/)) strength++;
        if (password.match(/[0-9]/)) strength++;
        if (password.match(/[^a-zA-Z0-9]/)) strength++;

        strengthFill.className = 'strength-fill';
        if (strength <= 1) {
            strengthFill.classList.add('weak');
            strengthText.textContent = 'Faible';
        } else if (strength === 2) {
            strengthFill.classList.add('medium');
            strengthText.textContent = 'Moyen';
        } else {
            strengthFill.classList.add('strong');
            strengthText.textContent = 'Fort';
        }
    });

    // Initialisation
    showStep(1);
});
//
