t.script:
// Script pour rendre la FAQ interactive
document.addEventListener('DOMContentLoaded', function() {
    // FAQ Accordion
    const faqItems = document.querySelectorAll('.ifn-faq-item');
    faqItems.forEach(item => {
        const question = item.querySelector('.ifn-faq-question');
        const answer = item.querySelector('.ifn-faq-answer');
        const icon = question.querySelector('i');

        question.addEventListener('click', function() {
            // Toggle current item
            item.classList.toggle('active');
            if (answer.style.display === 'block') {
                answer.style.display = 'none';
                icon.style.transform = 'rotate(0deg)';
            } else {
                answer.style.display = 'block';
                icon.style.transform = 'rotate(180deg)';
            }
        });
    });

    // Smooth scroll pour les ancres
    const links = document.querySelectorAll('a[href^=\"#"]');
    links.forEach(link => {
        link.addEventListener('click', function(e) {
            e.preventDefault();
            const targetId = this.getAttribute('href').substring(1);
            const targetElement = document.getElementById(targetId);
            if (targetElement) {
                targetElement.scrollIntoView({
                    behavior: 'smooth',
                    block: 'start'
                });
            }
        });
    });

    // Animation de recherche (placeholder)
    const searchInput = document.getElementById('helpSearch');
    if (searchInput) {
        const placeholders = [
            'Rechercher une réponse...',
            'Comment créer mon compte ?',
            'Gérer mes stocks...',
            'Suivre mes ventes...',
            'Devenir producteur...'
        ];
        let index = 0;

        setInterval(function() {
            searchInput.placeholder = placeholders[index];
            index = (index + 1) % placeholders.length;
        }, 3000);
    }

    // Animation d'apparition au scroll
    const observerOptions = {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver(function(entries) {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.style.opacity = '1';
                entry.target.style.transform = 'translateY(0)';
            }
        });
    }, observerOptions);

    // Observer les cartes
    document.querySelectorAll('.ifn-category-card, .ifn-topic-card, .ifn-article-item').forEach(el => {
        el.style.opacity = '0';
        el.style.transform = 'translateY(20px)';
        el.style.transition = 'all 0.6s ease';
        observer.observe(el);
    });
});
