t.script:
document.addEventListener('DOMContentLoaded', function() {
    // Animation des cartes de type de membre
    const typeCards = document.querySelectorAll('.type-card');
    const radioInputs = document.querySelectorAll('input[name="member_type"]');

    typeCards.forEach(card => {
        const radio = card.querySelector('input[type="radio"]');

        // Animation au clic
        card.addEventListener('click', function() {
            // Retirer la classe active de toutes les cartes
            typeCards.forEach(c => c.classList.remove('active'));

            // Ajouter la classe active √† la carte cliqu√©e
            this.classList.add('active');

            // Cocher le bouton radio correspondant
            radio.checked = true;

            // Animation de rebond
            this.style.transform = 'scale(1.05)';
            setTimeout(() => {
                this.style.transform = '';
            }, 200);
        });

        // Animation au survol
        card.addEventListener('mouseenter', function() {
            if (!radio.checked) {
                this.style.transform = 'translateY(-8px)';
            }
        });

        card.addEventListener('mouseleave', function() {
            if (!radio.checked) {
                this.style.transform = '';
            }
        });
    });

    // Gestion de l'√©tat initial (si un radio est pr√©-s√©lectionn√©)
    if (radioInputs.length > 0) {
        radioInputs.forEach(radio => {
            if (radio.checked) {
                radio.closest('.type-card').classList.add('active');
            }
        });
    }

    // Animation des champs de formulaire
    const formInputs = document.querySelectorAll('input, select');
    formInputs.forEach(input => {
        input.addEventListener('focus', function() {
            this.closest('.form-field')?.classList.add('focused');
        });

        input.addEventListener('blur', function() {
            this.closest('.form-field')?.classList.remove('focused');
        });
    });

    // Validation en temps r√©el du mot de passe
    const passwordField = document.getElementById('password');
    const confirmPasswordField = document.getElementById('confirm_password');

    if (passwordField && confirmPasswordField) {
        function validatePassword() {
            if (passwordField.value !== confirmPasswordField.value) {
                confirmPasswordField.setCustomValidity('Les mots de passe ne correspondent pas');
                confirmPasswordField.style.borderColor = '#dc3545';
            } else {
                confirmPasswordField.setCustomValidity('');
                confirmPasswordField.style.borderColor = '#28a745';
            }
        }

        passwordField.addEventListener('input', validatePassword);
        confirmPasswordField.addEventListener('input', validatePassword);
    }

    // Animation du bouton de soumission
    const submitBtn = document.querySelector('button[type="submit"]');
    const form = document.getElementById('ifn-signup-form');

    if (submitBtn && form) {
        form.addEventListener('submit', function(e) {
            if (!this.checkValidity()) {
                e.preventDefault();
                e.stopPropagation();

                // Animation de shake pour les champs invalides
                const invalidFields = this.querySelectorAll(':invalid');
                invalidFields.forEach(field => {
                    field.style.animation = 'shake 0.5s';
                    setTimeout(() => {
                        field.style.animation = '';
                    }, 500);
                });

                return;
            }

            // Animation de chargement
            submitBtn.innerHTML = 'Cr√©ation en cours...';
            submitBtn.disabled = true;
            submitBtn.style.opacity = '0.8';
        });
    }

    // Animation d'apparition progressive
    const observerOptions = {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver(function(entries) {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.style.opacity = '1';
                entry.target.style.transform = 'translateY(0)';
            }
        });
    }, observerOptions);

    // Observer les sections pour l'animation
    document.querySelectorAll('.slide-up').forEach(section => {
        section.style.opacity = '0';
        section.style.transform = 'translateY(30px)';
        section.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
        observer.observe(section);
    });

    // Am√©lioration de l'accessibilit√© clavier
    typeCards.forEach(card => {
        card.setAttribute('tabindex', '0');
        card.addEventListener('keydown', function(e) {
            if (e.key === 'Enter' || e.key === ' ') {
                e.preventDefault();
                this.click();
            }
        });
    });

    // Animation de particules flottantes (si support√©)
    if (CSS.supports('animation', 'float 1s ease-in-out')) {
        const createFloatingParticle = () => {
            const particle = document.createElement('div');
            particle.style.cssText = `
                position: fixed;
                width: 6px;
                height: 6px;
                background: rgba(247, 127, 0, 0.6);
                border-radius: 50%;
                pointer-events: none;
                z-index: 1000;
                left: ${Math.random() * window.innerWidth}px;
                top: ${window.innerHeight + 10}px;
                animation: float 6s linear infinite;
            `;
            document.body.appendChild(particle);

            setTimeout(() => {
                particle.remove();
            }, 6000);
        };

        // Cr√©er une particule toutes les 3 secondes
        setInterval(createFloatingParticle, 3000);
    }
});

// CSS pour l'animation shake
const shakeKeyframes = `
    @keyframes shake {
        0%, 100% { transform: translateX(0); }
        25% { transform: translateX(-5px); }
        75% { transform: translateX(5px); }
    }

    @keyframes float {
        0% {
            transform: translateY(0) rotate(0deg);
            opacity: 0;
        }
        10% {
            opacity: 1;
        }
        90% {
            opacity: 1;
        }
        100% {
            transform: translateY(-100vh) rotate(360deg);
            opacity: 0;
        }
    }

    @keyframes pulse {
        0%, 100% {
            transform: scale(1);
        }
        50% {
            transform: scale(1.05);
        }
    }
`;

// Ajouter les keyframes au document
const styleSheet = document.createElement('style');
styleSheet.textContent = shakeKeyframes;
document.head.appendChild(styleSheet);
